// Fade-in effect

var cookiez = Cookies.get('fadein');
var viewedAPI = Cookies.get('viewed');

if(typeof cookiez === 'undefined' || cookiez != 'true') {
    Cookies.set('fadein', 'true', { expires: 1, path: '' });
    var fancybv = $('header, footer');
    fancybv.css('visibility','hidden');
    bvapp.show();
    setTimeout(function(){
        fancybv.css({opacity: 0.0, visibility: "visible"}).delay(1000).animate({opacity: 1}, 2000);
    }, 800)
    setTimeout(function() {
        Cookies.set('viewed', 'true', { expires: 1, path: '' });
    }, 4000)
}

if(viewedAPI == 'true') {
    bvapp.showInstantly();
}

var el = document.querySelector('.bv');

var options = {
    size: 200,
    weight: 8,
    rounded: true,
    color: '#003D4C',
    duration: 1.5,
    delay: [0, 1],
    fade: 1,
    easing: d3_ease.easeCubicInOut.ease,
    individualDelays: true,
};

var bvapp = new Letters(el, options);
bvapp.show();
